import React,{useEffect, useState} from 'react'
import {useDispatch} from "react-redux"
import jwtDecode from "jwt-decode"
import {logout,login} from "../actions"
import "./Styles/home.css"
import axios from "axios"
import Guest from "../Components/guests"

function Home() {
    const dispatch = useDispatch()
    const [guests,setGuests] = useState([])
    const loadGuests = () => {
        axios.get("/getGuests",{headers: {'Authorization':localStorage.Authorization}})
        .then(data=> {
            console.log(data)
            console.log("Success!")
        }).catch(err=>{
            console.log("ERRRRROOOOOORRRR:"+err.code)
            if (err.code === 403) {
                window.location.href="/login"
            }
        })
    }

    useEffect(()=> {
        if (localStorage.Authorization) {
            if (jwtDecode(localStorage.Authorization.split("Bearer ")[1]).exp > Date.now()) {
                window.location.href = "/login"
                dispatch(logout())
            } else dispatch(login())
        } else {
            window.location.href = "/login"
        }

        loadGuests()
    })

    return (
        <div className="home">
            <label>Search Guest</label>
            <input type="text" />
            <button >Search</button>
            <div className="mainBody">
                {guests.map((guest)=> 
                    <Guest name={guest.name} attending={guest.attending}/>
                )}
            </div>
        </div>
    )
}

export default Home
