import React,{Component} from 'react'
import jwtDecode from "jwt-decode"
import "./Styles/home.css"
import axios from "axios"
import Guest from "../Components/guests"

class Home extends Component {
    state = {
        guests: [],
        latestParty: 0,
        searchName: "",
        searchedGuests: [],
        addGuest: ""
    }
    loadGuests = () => {
        axios.get("/getGuests",{headers: {'Authorization':localStorage.Authorization}})
        .then(data=> {
            this.setState({guests: data.data.guests})
            console.log("Success!")
            this.setState({latestParty: 
                data.data.guests.sort((a,b) => {
                    return a.party-b.party
                })[data.data.guests.length-1].party
            })
            console.log(this.state.latestParty)
        }).catch(err=>{
            if (err === "Error: Request failed with status code 403") {
                window.location.href="/login"
            }
        })
    }

    capitalize = (name) => {
        const nameList = name.split(' ')
        const newNameList = []
        nameList.forEach(n => {
            const newName = n[0].toUpperCase() + n.substring(1, name.length)
            newNameList.push(newName)
        })
        return newNameList.join('%20')
    }

    searchName=()=>{
        const name = this.capitalize(this.state.searchName)
        console.log(name)
        axios.get(`/guests/${name}`)
        .then(res=> {
            this.setState({searchedGuests: res.data.guests})
            console.log(res)
            console.log(this.state.searchedGuests)
        })
        .catch(err=> {
            console.log(err)
        })
    }

    textChange = (e) => {
        this.setState({
            [e.target.name]:e.target.value
        })
    }

    componentDidMount() {
        const decodedToken = jwtDecode(localStorage.Authorization.split("Bearer ")[1])
        if (localStorage.Authorization) {
            if (decodedToken.exp *1000 < Date.now()) {    
                window.location.href = "/login"
            }
        } else {
            window.location.href = "/login"
        }

        this.loadGuests()
    }

    render() {
        return (
            <div className="home">
                <label>Search Guest</label>
                <input type="text" placeholder="Search" name="searchName" value={this.state.searchName} onChange={this.textChange}/>
                <button onClick={this.searchName}>Search</button>
                <div className="mainBody">
                    <div style={{overflow:"auto", height: "500px",width: "100%"}}>
                    {this.state.guests.map((guest)=> 
                        <Guest key={guest.docId} name={guest.name} attending={guest.attending}/>
                    )}
                    </div>
                </div>
                <input type="text" placeholder="Add Guest" name="addGuest" value={this.state.addGuest} onChange={this.textChange}/>
            </div>
        )
    }
}

export default Home
