import React, { Component } from 'react'
import Guest from '../components/guest'
import Button from '@material-ui/core/Button'
import axios from 'axios'

class Rsvp extends Component {
    constructor() {
        super()
        this.state={
            nameFound: false,
            search: '',
            partyAttendees: []
        }
    }

    searchChange = (event) => {
        const {name, value} = event.target
        this.setState({
            [name]: value
        })
    }

    searchName = () => {
        const name = this.state.search.replace(' ', '%20')
        axios.get(`https://us-central1-jenawedding-ecede.cloudfunctions.net/api/guests/${name}`)
        .then((res) => {
            this.setState({
                nameFound: true
            })
            res.forEach(pers => {
                this.state.partyAttendees.push(
                    {
                        name: pers.name,
                        attending: pers.attending
                    }
                )
            })

        })
        .catch(err => {
            console.error(err)
        })
    }

    render() {
        const {nameFound} = this.state
        return (
            <div>
                {nameFound?
                <div>

                </div>
                :
                <div>
                    <input onChange={this.searchChange} value={this.state.search} name='search' type='text' placeholder='Search your name to RSVP here' />
                    <Button onClick={this.searchName} variant="contained">Search</Button>
                </div>
                }
            </div>
        )
    }
}

export default Rsvp
