import React, { Component } from 'react'
import Guest from '../components/guest'
import Button from '@material-ui/core/Button'
import axios from 'axios'

class Rsvp extends Component {
    constructor() {
        super()
        this.state={
            nameFound: false,
            search: '',
            partyAttendees: []
        }
    }

    searchChange = (event) => {
        const {name, value} = event.target
        this.setState({
            [name]: value
        })
    }

    searchName = () => {
        const name = this.state.search.replace(' ', '%20')
        axios.get(`/guests/${name}`)
        .then((res) => {
            this.setState({
                nameFound: true,
                partyAttendees: res.data.members
            })
        })
        .catch(err => {
            console.error(err)
        })
    }

    submitRsvp = (e) => {
        const {partyAttendees} = this.state
        const list = partyAttendees
        for (i=0; i < partyAttendees.length; i++) {
            if (partyAttendees[i].name == e.target.name && partyAttendees[i].attending !== e.target.name) {
                list[i].attending = !e.target.attending
                this.setState({partyAttendees: list})
            }
        }
    }

    render() {
        const {nameFound} = this.state
        return (
            <div>
                {nameFound?
                <div>
                    {this.state.partyAttendees.map(pers => {
                        return <Guest onClick={this.submitRsvp} attending={pers.attending} name={pers.name} />
                    })}
                </div>
                :
                <div>
                    <input onChange={this.searchChange} value={this.state.search} name='search' type='text' placeholder='Search your name to RSVP here' />
                    <Button onClick={this.searchName} variant="contained">Search</Button>
                </div>
                }
            </div>
        )
    }
}

export default Rsvp
