import React, { Component } from 'react'
import '../App.css'
import Button from '@material-ui/core/Button'


class Guest extends Component {
    state = {
        attending: this.props.attending,
        name: this.props.name
    }

    onClick = (e) => {
        this.setState(() => {
            if (e.target.name === 'rsvp') {
                return {attending: true}
            } else {
                return {attending: false}
            }
        })
    }

    render() {
        const {name, attending} = this.state
        return (
            <div>
                <h4 className='playfair center'>{name}</h4>
                <Button onClick={this.onClick} name='rsvp' variant={attending ? 'contained' : 'outlined'} >RSVP</Button>
                <Button onClick={this.onClick} name='norsvp' variant={attending ? 'outlined':'contained'} >Not Going</Button>
            </div>
        )
    }
}

export default Guest
