import React, { Component } from 'react'
import '../App.css'
import '../Rsvp.css'
import Button from '@material-ui/core/Button'
import ButtonGroup from '@material-ui/core/ButtonGroup';

// Return object with name, attending, party
/*
    Return {
        party: this.props.party,
        name: this.props.name,
        attending: this.props.attending
    }
*/

class Guest extends Component {

    sendData = () => {
        this.props.callback({
            party: this.props.party,
            attending: !this.props.attending,
            name: this.props.name
        })
    }

    render() {
        const {name, attending} = this.props
        return (
            <div>
                <h4 className='playfair center'>{name}</h4>
                <div className='container'>
                <ButtonGroup variant="text" aria-label="text rgb(20,50,150) button group">
                <Button disabled={this.props.attending} onClick={this.sendData}>RSVP</Button>
                <Button disabled={this.props.attending} onClick={this.sendData}>Not Going</Button>
                </ButtonGroup>
                </div>
            </div>
        )
    }
}

export default Guest
