import React, { Component } from 'react'
import '../App.css'
import Button from '@material-ui/core/Button'

const selected = {
    color: 'rgb(253, 219, 0)'
}

const unselected = {
    color: 'rgb(253, 150, 0)'
}

class Guest extends Component {

    render() {
        const {name, attending} = this.props
        return (
            <div>
                <h4 className='playfair center'>{name}</h4>
                <form onSubmit={(e)=>{e.preventDefault()}}>
                    <Button variant='outlined' style={ attending ? selected : unselected}>RSVP</Button>
                    <Button variant='outlined' style={ attending ? unselected : selected}>Not Going</Button>
                </form>
            </div>
        )
    }
}

export default Guest
