import React, { Component } from 'react'
import Guest from '../components/guest'
import Button from '@material-ui/core/Button'
import axios from 'axios'

class Rsvp extends Component {
    constructor() {
        super()
        this.state={
            nameFound: false,
            search: '',
            partyAttendees: []
        }
    }

    searchChange = (event) => {
        const {name, value} = event.target
        this.setState({
            [name]: value
        })
    }

    searchName = () => {
        const name = this.state.search.replace(' ', '%20')
        axios.get(`https://us-central1-jenawedding-ecede.cloudfunctions.net/api/guests/${name}`)
        .then((res) => {
            console.log(res.data)
            this.setState({
                nameFound: true,
                partyAttendees: res.data
            })
        })
        .catch(err => {
            console.error(err)
        })
    }

    render() {
        const {nameFound} = this.state
        return (
            <div>
                {nameFound?
                <div>
                    {this.state.partyAttendees.map(pers => {
                        return <Guest attending={pers.name} name={pers.name} />
                    })}
                </div>
                :
                <div>
                    <input onChange={this.searchChange} value={this.state.search} name='search' type='text' placeholder='Search your name to RSVP here' />
                    <Button onClick={this.searchName} variant="contained">Search</Button>
                </div>
                }
            </div>
        )
    }
}

export default Rsvp
