import React, { Component } from 'react'
import Guest from '../components/guest'
import Button from '@material-ui/core/Button'
import axios from 'axios'

const style = {
    display: 'inline-block',
    width: '60%'
}

class Rsvp extends Component {
    constructor() {
        super()
        this.state={
            nameFound: false,
            search: '',
            partyAttendees: []
        }
    }

    searchChange = (event) => {
        const {name, value} = event.target
        this.setState({
            [name]: value
        })
    }

    searchName = () => {
        const name = this.state.search.replace(' ', '%20')
        axios.get(`/guests/${name}`)
        .then((res) => {
            this.setState({
                nameFound: true,
                partyAttendees: res.data.members
            })
        })
        .catch(err => {
            console.error(err)
        })
    }


    render() {
        const {nameFound} = this.state
        const searched = nameFound ?
                <div style={style}>
                    {this.state.partyAttendees.map((pers,i) => {
                        return <Guest key={i} attending={pers.attending} name={pers.name} />
                    })}
                </div>
                :
                <div style={style}>
                    <input onChange={this.searchChange} value={this.state.search} name='search' type='text' placeholder='Search your name to RSVP here' />
                    <Button onClick={this.searchName} variant="contained">Search</Button>
                </div>
        return (
            <div style={{textAlign: 'center'}}>
               {searched}
            </div>
        )
    }
}

export default Rsvp
